import React, { useState } from "react";
import CarouselSet from "./carouselSet";

const Carousel = () => {
  const [state, setState] = useState({
    restaurantSet: [["rest1", "rest2", "rest3"], ["rest4", "rest5", "rest6"]],
    currentSet: [],
    index: 0
  });

  const nextSet = () => {
    // set state
    setState(state => ({
      ...state,
      currentSet:
        state.index == state.restaurantSet.length - 1
          ? state.restaurantSet[0]
          : state.restaurantSet[state.index + 1],
      index: state.index == state.restaurantSet.length - 1 ? 0 : state.index++
    }));
  };

  const prevSet = () => {
    // set state
    setState(state => ({
      ...state,
      currentSet:
        state.index == 0
          ? state.restaurantSet[state.restaurantSet.length - 1]
          : state.restaurantSet[state.index - 1],
      index: state.index == state.restaurantSet.length - 1 ? 0 : state.index++
    }));
  };

  return (
    <div className="carousel">
      <button className="prevCarouselBtn" onClick={prevSet}>
        &#8249;
      </button>
      <CarouselSet viewSet="currentSet" />
      <button className="nextCarouselBtn" onClick={nextSet}>
        &#8250;
      </button>
    </div>
  );
};

export default Carousel;
