import React, { Component, useState } from "react";
import { CSSTransition, TransitionGroup } from "react-transition-group";
import CarouselSet from "./carouselSet";
import "./carousel.css";

const Carousel = () => {
  const [state, setState] = useState({
    restaurantSet: [
      ["rest1", "rest2", "rest3"],
      ["rest4", "rest5", "rest6"],
      ["rest7", "rest8", "rest9"]
    ],
    index: 0,
    newIndex: 0
  });

  const nextSet = () => {
    // set state
    setState(state => ({
      ...state,
      index: state.newIndex,
      newIndex: (state.index + 1) % state.restaurantSet.length
    }));
  };

  const prevSet = () => {
    // set state
    setState(state => ({
      ...state,
      index: state.newIndex,
      newIndex:
        (state.index - 1 + state.restaurantSet.length) %
        state.restaurantSet.length
    }));
  };

  return (
    <div className="carousel">
      <p className="header">Suggested for You</p>

      <button className="prevCarouselBtn" onClick={prevSet}>
        &#8249;
      </button>
      <button className="nextCarouselBtn" onClick={nextSet}>
        &#8250;
      </button>

      <TransitionGroup>
        <CSSTransition
          key={state.index}
          timeout={1000}
          classNames={"prevTransition"}
        >
          <div className="carouselSet">
            <CarouselSet viewSet={state.restaurantSet[state.index]} />
          </div>
        </CSSTransition>
      </TransitionGroup>
    </div>
  );
};

export default Carousel;
